// Queries changes in LastName of Contacts
{
    "sessionId": "{{SID}}",
    "query": {
        "__type": "MainTableQuery:#EQ",
        "ItemTypes": [
            "History"
        ],
        "Fields": [
            {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "Name": "ModifiedByGUID"
            },
            {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "Name": "ModifiedItemGUID"
            },
            {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "Name": "PrevValueVarChar"
            },
            {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "Name": "NewValueVarChar"
            }
        ],
        "Filter": {
            "__type": "AndFilterExpressionOperator:#EQ",
            "Children": [
                {
                    "__type": "EqualsFilterExpressionPredicate:#EQ",
                    "Field": {
                        "__type": "Column:#EQ",
                        "Source": {
                            "__type": "MainTable:#EQ"
                        },
                        "Name": "ColumnName"
                    },
                    "Value": "LastName"
                },
                {
                    "__type": "EqualsFilterExpressionPredicate:#EQ",
                    "Field": {
                        "__type": "Column:#EQ",
                        "Source": {
                            "__type": "MainTable:#EQ"
                        },
                        "Name": "ObjectTypeID"
                    },
                    "Value": "9"
                }
            ]
        },
        "Paging": {
            "Skip": 0,
            "Take": 100
        }
    }
}

