// Queries last activity and last login of active non-system users
// Aggregation functions: Count = 0, Avg = 1, Min = 2, Max = 3, Sum = 4
{
    "sessionId": "{{SID}}",
    "query": {
        "__type": "MainTableQuery:#EQ",
        "ItemTypes": [
            "Users"
        ],
        "Fields": [
            {
                "__type": "AggregateColumn:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "AggregatedField": {
                    "__type": "Column:#EQ",
                    "Name": "Server_LastActivity",
                    "Source": {
                        "__type": "MainTable:#EQ"
                    }
                },
                "Function": 3,
                "Alias": "LastActivity" // Alias is required
            },
            {
                "__type": "AggregateColumn:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "AggregatedField": {
                    "__type": "Column:#EQ",
                    "Name": "Server_LastLogin",
                    "Source": {
                        "__type": "MainTable:#EQ"
                    }
                },
                "Function": 3,
                "Alias": "LastLogin"
            }
        ],
        // Sort needs to be always defined otherwise data are sorted by Server_ID
        "Sort":[
            {
                "Field": {
                    "__type": "AggregateColumn:#EQ",
                    "Source": {
                        "__type": "MainTable:#EQ"
                    },
                    "AggregatedField": {
                        "__type": "Column:#EQ",
                        "Name": "Server_LastActivity",
                        "Source": {
                            "__type": "MainTable:#EQ"
                        }
                    },
                    "Function": 3,
                    "Alias": "LastActivity" // Alias is required
                },
                "OrderDirection": "Descending"
            }
        ],
        "Filter": {
            "__type": "AndFilterExpressionOperator:#EQ",
            "Children": [
                {
                    "__type": "EqualsFilterExpressionPredicate:#EQ",
                    "Field": {
                        "__type": "Column:#EQ",
                        "Source": {
                            "__type": "MainTable:#EQ"
                        },
                        "Name": "Active"
                    },
                    "Value": "1"
                },
                {
                    "__type": "EqualsFilterExpressionPredicate:#EQ",
                    "Field": {
                        "__type": "Column:#EQ",
                        "Source": {
                            "__type": "MainTable:#EQ"
                        },
                        "Name": "IsSystem"
                    },
                    "Value": "0"
                }
            ]
        },
        "Paging": {
            "Skip": 0,
            "Take": 1
        }
    }
}

