// Queries invoices, joins EnumValues to get only specific item type
{
    "sessionId": "{{SID}}",
    "query": {
        "__type": "MainTableQuery:#EQ",
        "ItemTypes": [
            "Carts"
        ],
        "Fields": [
            {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "Name": "ItemGUID"
            },
            {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "MainTable:#EQ"
                },
                "Name": "FileAs"
            },
            {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "Join:#EQ",
                    "ItemType": "EnumValues",
                    "Key": {
                        "__type": "Column:#EQ",
                        "Source": {
                            "__type": "MainTable:#EQ"
                        },
                        "Name": "TypeEn"
                    },
                    "TargetColumnName": "ItemGUID"
                },
                // English translation of the Cart Type
                "Name": "en"
            }
        ],
        "Filter": {
            "__type": "EqualsFilterExpressionPredicate:#EQ",
            "Field": {
                "__type": "Column:#EQ",
                "Source": {
                    "__type": "Join:#EQ",
                    "ItemType": "EnumValues",
                    "Key": {
                        "__type": "Column:#EQ",
                        "Source": {
                            "__type": "MainTable:#EQ"
                        },
                        "Name": "TypeEn"
                    },
                    "TargetColumnName": "ItemGUID"
                },
                "Name": "FileAs"
            },
            // Filter by FileAs (key)
            "Value": "CartType - Sales invoice"
        },
        "Paging": {
            "Skip": 0,
            "Take": 100
        }
    }
}
